class Prices extends React.Component {
  state = {
    currency: "GBP",
  };
  render() {
    let list = "";
    if (this.state.currency === "USD") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.USD.description} :
          <span className="badge badge-primary">
            {this.props.bpi.USD.code} <strong>{this.props.bpi.USD.rate}</strong>
          </span>
        </li>
      );
    } else if (this.state.currency === "GBP") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.GBP.description} :
          <span className="badge badge-primary">
            {this.props.bpi.GBP.code} <strong>{this.props.bpi.GBP.rate}</strong>
          </span>
        </li>
      );
    } else if (this.state.currency === "EUR") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.EUR.description} :
          <span className="badge badge-primary">
            {this.props.bpi.EUR.code} <strong>{this.props.bpi.EUR.rate}</strong>
          </span>
        </li>
      );
    }

    return (
      <div>
        <ul className="list-group">{list}</ul>
        <br />
        <select
          onChange={(e) => this.setState({ currency: e.target.value })}
          className="form-control"
        >
          <option>sa</option>
        </select>
      </div>
    );
  }
}

export default Prices;
